import collections
from collections import Counter, deque


class Solution(object):
    def minWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        # get counts of chars in t
        t_counts = Counter(t)
        # create a counter for keeping track of chars in w
        w = Counter()
        # keep track of shortest answer found so far
        r = ''
        # keep track of which characters we have in the current window
        window = deque()
        for ch in s:
            # add current character to window
            window.append(ch)
            # increment count in window
            w[ch] += 1
            # check if predicate is satisfied (window contains all chars in t)
            if all(w[c] >= t_counts[c] for c in t_counts.keys()):
                # remove unnecessary (superfluous) chars
                while window and w[window[0]] > t_counts[window[0]]:
                    w[window.popleft()] -= 1
                # record this new answer only if it is shorter than a previous answer
                # (or if no previous answer exists)
                if r == '' or len(window) < len(r):
                    r = ''.join(window)
                # remove the last added char so we can keep looking for more substrings
                if window:
                    w[window.popleft()] -= 1

        return r

    def minWindow2(self, s, t):
        if len(s) < len(t):
            return ""
        needstr = collections.defaultdict(int)
        for ch in t:
            needstr[ch] += 1
        needcnt = len(t)
        res = (0, float('inf'))
        start = 0
        for end, ch in enumerate(s):
            if needstr[ch] > 0:
                needcnt -= 1
            needstr[ch] -= 1
            if needcnt == 0:
                while True:
                    tmp = s[start]
                    if needstr[tmp] == 0:
                        break
                    needstr[tmp] += 1
                    start += 1
                if end - start < res[1] - res[0]:
                    res = (start, end)
                needstr[s[start]] += 1
                needcnt += 1
                start += 1
        return '' if res[1] > len(s) else s[res[0]:res[1] + 1]


print(Solution().minWindow2(s="asdasdBANCasd", t="ABC"))
print(Solution().minWindow2(s="a", t="a"))
print(Solution().minWindow2(s="a", t="aa"))
print(Solution().minWindow2(s="",
                           t=""))
